mapper.select:
select 

from produce_info where 1=1

	and type = #{type} 


	and grade = #{grade} 


	and spyb = #{spyb} 


	and parent_id = #{parent_id} 


	and status = #{status} 


	and nowtime = #{nowtime}

select 

from produce_info where 1=1

	and type = #{type} 


	and grade = #{grade} 


	and spyb = #{spyb} 


	and parent_id = #{parent_id} 


	and status = #{status} 


	and nowtime = #{nowtime} 

order by parentid, createtime desc
limit ${index},8

select 
count(1) as num
from produce_info where 1=1

	and type = #{type} 


	and grade = #{grade} 


	and spyb = #{spyb} 


	and parent_id = #{parent_id} 


	and status = #{status} 


	and nowtime = #{nowtime}

    SELECT max(type)as type,max(grade) as grade,max(spyb)as spyb,sum(supply_number)as supply_number,AVG(price) as price,
	left(MAX(createtime),10)as createtime,max(status) as status
	from produce_info
	where createtime like '%${nowDay}%' and status = #{status}
	and parent_id = #{parentid}
	 GROUP BY type,grade,spyb

mapper.insert:
  	insert into purchase_info (purchase_id,kind,supply_number,grade,spyb,createtime,status,parentid,type,price)
	select replace(UUID(),'-',''),type,supply_number,grade,spyb,now(),'0',#{cg_parentid},'0',price from produce_info
	WHERE parent_id = #{gy_parentid}
