mapper.select:
select 

from purchase_info
where 1=1

	and createtime like '%${createtime}%'


	and createuser = #{createuser}


	and status = #{status}


	and parentid = #{parentid}


	and grade = #{grade}


	and kind = #{kind}


	and type = #{type}


	and spyb = #{spyb}

select 

from purchase_info
where 1=1

	and createtime like '%${createtime}%'


	and createuser = #{createuser}


	and status = #{status}


	and parentid = #{parentid}


	and grade = #{grade}


	and kind = #{kind}


	and type = #{type}


	and spyb = #{type}

limit ${index},8
order by createtime desc

