mapper.select:
select 

from purchase_info
where 1=1

	and createtime like '%${createtime}%'


	and createuser = #{createuser}


	and status = #{status}


	and parentid = #{parentid}


	and grade = #{grade}


	and kind = #{kind}


	and type = #{type}


	and spyb = #{spyb}

    select 
    
    from purchase_info p
    where EXISTS 
	(select 1 from test t where t.test_kind=p.kind  and t.test_grade=p.grade 
	and t.test_spyb = p.spyb and t.test_result='1'
	and t.isQh ='1' and t.test_status = '2' and LEFT(t.test_time,10)=left(now(),10))
    
    	and p.createtime like '%${createtime}%'
    
    
    	and p.createuser = #{createuser}
    
    
    	and p.status = #{status}
    
    
    	and p.parentid = #{parentid}
    
    
    	and p.grade = #{grade}
    
    
    	and p.kind = #{kind}
    
    
    	and p.type = #{type}
    
    
    	and p.spyb = #{spyb}

select 

from purchase_info
where 1=1

	and createtime > date_sub(now(), interval 15 hour)


	and createuser = #{createuser}


	and status = #{status}


	and parentid = #{parentid}


	and grade = #{grade}


	and kind = #{kind}


	and type = #{type}


	and spyb = #{spyb}

select 

from purchase_info
where 1=1

	and createtime like '%${createtime}%'


	and createuser = #{createuser}


	and status = #{status}


	and parentid = #{parentid}


	and grade = #{grade}


	and kind = #{kind}


	and type = #{type}


	and spyb = #{type}

limit ${index},${size}
order by createtime desc

