mapper.select:
select 

from trace_flow where 1=1

	and test_id = #{test_id}


	and farmer_id = #{farmer_id}


	and purchase_id = #{purchase_id}


	and sampling_id = #{sampling_id}


	and sale_id = #{sale_id}


	and trace_status = #{trace_status}


	and qrcode = #{qrcode}


	and createtime like '%${createtime}%'


	and transport_id = #{transport_id}

SELECT * from trace_flow tf,farmer f,purchase p,test t,transport tra,qrcode q WHERE f.farmer_id = tf.farmer_id and p.purchase_id = tf.purchase_id and t.test_id = tf.test_id and tra.transport_id = tf.transport_id and q.qrcode_id = tf.qrcode and tf.trace_status = '6'
and tf.createtime >= #{starttime}
and tf.createtime <= #{endtime}
and EXISTS (SELECT 1 from farmer f where f.farmer_name like '%${farmer_name}%' and f.farmer_id=tf.farmer_id)
ORDER BY tf.trace_status,tf.createtime desc limit ${index},8

  	SELECT count(1) as num
	 from trace_flow t
	 where 1=1

	 	t.createtime >= #{starttime}


	 	t.createtime <= #{endtime}


	 	 and EXISTS (SELECT 1 from farmer f where f.farmer_name like '%${farmer_name}%' and f.farmer_id=t.farmer_id)

mapper.update:
UPDATE trace_flow
farmer_id=#{farmer_id}, purchase_id=#{purchase_id}, test_id=#{test_id}, sampling_id=#{sampling_id}, transport_id=#{transport_id}, trace_status=#{trace_status}, qrcode=#{qrcode}, sale_id=#{sale_id},
WHERE trace_id=#{trace_id}
