mapper.select:
SELECT *, (SELECT farmer_name from farmer f where f.farmer_id = t.farmer_id)as farmer_name, (SELECT purchase_name from purchase p where p.purchase_id = t.purchase_id)as purchase_name, (SELECT test_name from test where test.test_id = t.test_id)as test_name, (SELECT test_name from test where test.test_id = t.sampling_id)as sampling_name, (SELECT transport_destination from transport where transport.transport_id = t.transport_id)as transport_destination, (SELECT transport_user from transport where transport.transport_id = t.transport_id)as transport_user, (SELECT purchase_type from purchase where purchase.purchase_id = t.purchase_id)as purchase_type, (SELECT qrcode_path from qrcode where qrcode.qrcode_id = t.qrcode)as qrcode_path from trace_flow t where 1=1
t.createtime >= #{starttime}
t.createtime <= #{endtime}
and EXISTS (SELECT 1 from farmer f where f.farmer_name like '%${farmer_name}%' and f.farmer_id=t.farmer_id)
ORDER BY t.trace_status,t.createtime limit ${index},8

  	SELECT count(1) as num
	 from trace_flow t
	 where 1=1

	 	t.createtime >= #{starttime}


	 	t.createtime <= #{endtime}


	 	 and EXISTS (SELECT 1 from farmer f where f.farmer_name like '%${farmer_name}%' and f.farmer_id=t.farmer_id)

mapper.update:
UPDATE trace_flow
farmer_id=#{farmer_id}, purchase_id=#{purchase_id}, test_id=#{test_id}, sampling_id=#{sampling_id}, transport_id=#{transport_id}, trace_status=#{trace_status}, qrcode=#{qrcode}, sale_id=#{sale_id},
WHERE trace_id=#{trace_id}
